void mc_testbench::wait_on_input_required()
{
   ++wait_cnt;
   wait(SC_ZERO_TIME); // get fifos a chance to update
   ++period_counter;
   sc_time timeout = sc_time_stamp() - previous_timestamp;
   if (calculate_period && sc_time_stamp() > SC_ZERO_TIME && sc_time_stamp() != previous_timestamp && sc_time_stamp() != timeout) {
      average_period = (average_period + timeout) / 2;
   }
   previous_timestamp = sc_time_stamp();
   while (atleast_one_active_input) {
      if (_capture_input_real_m && ccs_input_real_m->used() == 0) return;
      if (_capture_input_real_e && ccs_input_real_e->used() == 0) return;
      if (_capture_input_imag_m && ccs_input_imag_m->used() == 0) return;
      if (_capture_input_imag_e && ccs_input_imag_e->used() == 0) return;
      if (_capture_taps_real_m && ccs_taps_real_m->used() == 0) return;
      if (_capture_taps_real_e && ccs_taps_real_e->used() == 0) return;
      if (_capture_taps_imag_m && ccs_taps_imag_m->used() == 0) return;
      if (_capture_taps_imag_e && ccs_taps_imag_e->used() == 0) return;
      that->cpp_testbench_active.write(false);
      if (average_period > SC_ZERO_TIME && sc_time_stamp() != timeout)
         wait(average_period * 10, ccs_input_real_m->ok_to_put() | ccs_input_real_e->ok_to_put() | ccs_input_imag_m->ok_to_put() | ccs_input_imag_e->ok_to_put() | ccs_taps_real_m->ok_to_put() | ccs_taps_real_e->ok_to_put() | ccs_taps_imag_m->ok_to_put() | ccs_taps_imag_e->ok_to_put());
      else
         wait(ccs_input_real_m->ok_to_put() | ccs_input_real_e->ok_to_put() | ccs_input_imag_m->ok_to_put() | ccs_input_imag_e->ok_to_put() | ccs_taps_real_m->ok_to_put() | ccs_taps_real_e->ok_to_put() | ccs_taps_imag_m->ok_to_put() | ccs_taps_imag_e->ok_to_put());
      that->cpp_testbench_active.write(true);
      if (timed_out()) {
         calculate_period = false;
         return;
      }
   }
}
// ============================================
// Function: capture_IN
// --------------------------------------------

void mc_testbench::capture_IN(my_complex_float_t input, my_complex_float_t taps[32])
{
   that->capture_input(input);
   that->capture_taps(taps);
}
// ============================================
// Function: capture_OUT
// --------------------------------------------

void mc_testbench::capture_OUT(my_complex_float_t _TOP__return, my_complex_float_t input, my_complex_float_t taps[32])
{
   that->capture__TOP__return(_TOP__return);
}
// ============================================
// Function: exec_fir
// --------------------------------------------

my_complex_float_t mc_testbench::exec_fir(my_complex_float_t input, my_complex_float_t taps[32])
{
   #ifndef CCS_SCVERIFY_USE_CCS_BLOCK
   wait_for_idle_sync();
   capture_IN(input, taps);
   my_complex_float_t _TOP__return = fir(input, taps);
   // throttle ac_channel based on number of calls to chan::size() or chan::empty() or chan::nb_read() (but not chan::available()) 
   if (1) {
      int cnt=0;
      if (cnt) std::cout << "mc_testbench.cpp: CONTINUES @ " << sc_time_stamp() << std::endl;
      if (cnt) that->cpp_testbench_active.write(true);
   }
   capture_OUT(_TOP__return, input, taps);
   return _TOP__return;
   #else
   my_complex_float_t _TOP__return;
   return _TOP__return;
   #endif
}
